                            value={searchQueryCon}
                            onChangeText={text => setSearchQueryCon(text)}
                        />
                    </View>
                    {filteredContractors.length !== 0 ? filteredContractors.map((item) => (
                        <TouchableOpacity
                            style={[styles.card, {width: cardSize * 0.92, height: cardSize * 0.28}]}
