            <TouchableOpacity style={styles.buyButton} onPress={() => {
              navigation.navigate('Purchase Option Fee', { propertyListing });
            }}>
              <Text style={styles.buttonTextUser}>Buy</Text>
            </TouchableOpacity>
          </>
        )}
      </View>

    </View>
  );
};


