import React from 'react';
import { StyleSheet, View, Text, Image, TouchableOpacity, Dimensions } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { RFValue } from 'react-native-responsive-fontsize';
import * as Google from 'expo-auth-session/providers/google';
import * as WebBrowser from 'expo-web-browser';
import AsyncStorage from '@react-native-async-storage/async-storage';

const { width, height } = Dimensions.get('window');

WebBrowser.maybeCompleteAuthSession();

const LoginSelection = () => {
  const navigation = useNavigation();
  const [accessToken, setAccessToken] = React.useState('');
  const [userInfo, setUserInfo] = React.useState(null);
  const [request, response, promptAsync] = Google.useAuthRequest({
    androidClientId: '975614309732-0ngvqr4vvcebrjdlpeldknq9mpr3a5mq.apps.googleusercontent.com',
    iosClientId: '975614309732-aef963hu3lkfus4u3jqnnkmm263vb7v7.apps.googleusercontent.com',
    webClientId: '975614309732-7efv821237vv7prh9oq73vek4bv2h4il.apps.googleusercontent.com',
  });

  React.useEffect(() => {
    if (response?.type === 'success') {
        setAccessToken(response.authentication.accessToken);
      const { id_token } = response.params;

      // Send the id_token to your backend for verification
      fetch('http://10.0.0.17:3000/auth/google-signin', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${id_token}`,
        },
      })
      .then(res => res.json())
      .then(data => {
        // Handle the user data response
        navigation.navigate('Home Page');
      })
      .catch(error => {
        console.error(error);
      });
    }
  }, [response]);

  async function fetchUserInfo() {
    let response = await fetch("https://www.googleapis.com/userinfo/v2/me", {
      headers: { Authorization: `Bearer ${accessToken}` }
    });
    const useInfo = await response.json();
    setUser(useInfo);
  }

  const ShowUserInfo = () => {
    if(user) {
      return(
        <View style={{flex: 1, alignItems: 'center', justifyContent: 'center'}}>
          <Text style={{fontSize: 35, fontWeight: 'bold', marginBottom: 20}}>Welcome</Text>
          <Image source={{uri: user.picture}} style={{width: 100, height: 100, borderRadius: 50}} />
          <Text style={{fontSize: 20, fontWeight: 'bold'}}>{user.name}</Text>
        </View>
      )
    }
  }

  return (
    <View style={styles.container}>
      <Text style={styles.welcomeText}>Welcome to PropertyGo!</Text>
      <Image source={require('../../assets/PropertyGo-HighRes-Logo.png')} style={styles.icon} />
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Login')}>
        <Text style={styles.buttonText}>Sign In with Email</Text>
      </TouchableOpacity>
      {/* Add the following button */}
      <TouchableOpacity style={styles.socialButton} onPress={() => promptAsync()}>
        <Text style={styles.socialButtonText}>Sign In with Google</Text>
      </TouchableOpacity>

    </View>
  );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#FFFFFF',
    },
    welcomeText: {
        fontWeight: 'bold',
        fontSize: RFValue(24, 680),
        color: 'black',
        marginBottom: 60,
    },
    icon: {
        width: width * 0.5,
        height: height * 0.2,
        resizeMode: 'contain',
        marginBottom: 80,
    },
    button: {
        alignItems: 'center',
        justifyContent: 'center',
        padding: 15,
        borderRadius: 25,
        backgroundColor: '#1E90FF',
        width: width * 0.8,
        marginVertical: 10,
    },
    buttonText: {
        color: '#fff',
        fontSize: RFValue(16, 680),
    },
    socialButton: {
        alignItems: 'center',
        justifyContent: 'center',
        padding: 15,
        borderRadius: 25,
        backgroundColor: '#4267B2',
        width: width * 0.8,
        marginVertical: 10,
    },
    socialButtonText: {
        color: '#fff',
        fontSize: RFValue(16, 680),
    },
});


export default LoginSelection;
